import React, { Component, PureComponent } from 'react';


class Article extends PureComponent {

    constructor(props) {
        super(props);
        this.state = {
            count: 0,
        }
        this.incrementCounter = incrementCounter.bind(this);
    }


    render() {
        console.log('----', 2);
        const { article, isOpen, onButtonClick } = this.props;
        console.log('-------------------->', isOpen);
        const body = isOpen && <section className="card-text">{article.text}</section>
        return (

            <div className="card mx-auto" style = {{ width:'50%'}}> 
            <div className="card-header">
                <h2>{article.title}&emsp;
                clicked {this.state.count}
            <button className="btn btn-primary btn-lg float-right" onClick={onButtonClick} onClick={ this.incrementCounter}>{isOpen ? 'Close' : 'Open'}></button>     

            </h2>
            </div>

        <div className="card-body">
        <h6 className="card-subtitle text-muted">
    creation date:{article.date} 
    </h6>
                    {body}

        </div>

   </div>

        )


    }
}


function incrementCounter() {
    this.setState({
        count: this.state.count + 1
    })

}


export default Article;
