import React, { Component } from 'react';


class Article extends Component {

    constructor(props) {
        super(props);
        this.state = {
            isOpen: props.defaultOpen,
            count: 0,
        }
        this.handleClick = handleClick.bind(this);
    }
    componentWillMount() {

        console.log('----', 'mounting');

    }

    componentWillReceiveProps(nextProps) {
        if (nextProps.defaultOpen !== this.props.defaultOpen) {
            this.setState({
                isOpen: nextProps.defaultOpen
            })
        }
    }

    shouldComponentUpddate(nextProps, nextState) {
        return this.state.isOpen !== nextState.isOpen
    }

    componentWillUpdate() {
        console.log('----', 'will update')
    }

    render() {

        const { article } = this.props;
        const body = this.state.isOpen && <section className="card-text">{article.text}</section>
        return (

            <div className="card mx-auto" style = {{ width:'50%'}}> 
            <div className="card-header">
                <h2 onClick={this.incrementCouter}>{article.title}
                clicked {this.state.count}
            <button className="btn btn-primary btn-lg float-right" onClick={this.handleClick}>{this.state.isOpen ? 'Close' : 'Open'}</button>     

            </h2>
            </div>

        <div className="card-body">
        <h6 className="card-subtitle text-muted">
    creation date:{article.date} 
    </h6>
                    {body}

        </div>

   </div>

        )


    }
}

function incrementCounter() {

    this.setState({

        count: this.state.count++

    });

}


function handleClick() {
    this.setState({
        isOpen: !this.state.isOpen
    })
    console.log(this.state);
}


export default Article;
